import nodemailer from 'nodemailer'

var obj = {};
obj.sendAccountVerificationEmail = function (params) {
  const transporter = nodemailer.createTransport({
    host: process.env.EMAIL_SERVER,
    port: process.env.EMAIL_PORT,
    auth: {
      user: process.env.EMAIL_SENDER,
      pass: process.env.EMAIL_PASSWORD
    }
  });
  transporter.sendMail({
    from: params.senderEmail || process.env.EMAIL_SENDER,
    to: params.email,
    subject: 'CloudHaven account email verification',
    html: `<html><h2>CloudHaven Account Email Verification</h2>

    
    <p><a href="${process.env.RESTSRVR_DOMAIN}/api/verifyaccount/${encodeURIComponent(params.email)}/${encodeURIComponent(params.code)}">
    Please click on this link</a> to complete your CloudHaven registration and enable your login.</p>
    </html>
  `
  })
  .then(result =>{

  })
  .catch(err =>{
    console.log('Email send error: '+err);
  })
}
obj.sendPasswordResetEmail = function (params) {
  const transporter = nodemailer.createTransport({
    host: process.env.EMAIL_SERVER,
    port: process.env.EMAIL_PORT,
    auth: {
      user: process.env.EMAIL_SENDER,
      pass: process.env.EMAIL_PASSWORD
    }
  });
  transporter.sendMail({
    from: params.senderEmail || process.env.EMAIL_SENDER,
    to: params.email,
    subject: 'CloudHaven password reset',
    html: `<html><h2>CloudHaven Password Reset</h2>

    
    <p><a href="${process.env.RESTSRVR_DOMAIN}/api/requestpwdreset/${encodeURIComponent(params.email)}/${encodeURIComponent(params.code)}">
    Please click on this link</a> to go to the CloudHaven password reset page.</p>
    </html>
  `
  })
  .then(result =>{

  })
  .catch(err =>{
    console.log('Email send error: '+err);
  })
}

obj.getPasswordResetForm = function( email, code ) {
  var url = `${process.env.RESTSRVR_DOMAIN}/api/resetpwd`;
  var dataURI = 'data:image/png;base64,';
  return `
  <!DOCTYPE html>
  <html>
  <head>
    <link href="https://fonts.googleapis.com/css?family=Roboto:100,300,400,500,700,900" rel="stylesheet">
    <link href="https://cdn.jsdelivr.net/npm/@mdi/font@5.x/css/materialdesignicons.min.css" rel="stylesheet">
    <link href="https://cdn.jsdelivr.net/npm/vuetify@2.x/dist/vuetify.min.css" rel="stylesheet">
    <meta name="viewport" content="width=device-width, initial-scale=1, maximum-scale=1, user-scalable=no, minimal-ui">
  </head>
  <body>
    <div id="app">
      <v-app>
        <v-main>
          <v-container class="justify-center">
        <v-row class="text-center justify-space-around" >
          <v-spacer></v-spacer>
          <v-col cols="4" class="px-1">
          <v-card height="650px" style="background: linear-gradient(to bottom, #FFFFFF -10%, #00528d 100%);" class="pt-9 ">
            <div class="pa-6"><v-img  :src="logoDataURI" /></div>
            <div>
            <span class='text-h2'><b>CloudHaven</b></span>
            </div>
          </v-card>
          </v-col>
          <v-col cols="4" class="px-1">
          <v-card height="650px" :style="{'background-color':'#00b397'}" class="d-flex justify-space-around">
            <v-card class="px-1 mt-auto mb-auto">
            <v-card-title>Password Reset Form</v-card-title>
            <v-card-text>
              <v-form ref="pwdResetForm" v-model="valid" lazy-validation>
                <v-text-field v-model="password" :append-icon="show1 ? 'mdi-eye' : 'mdi-eye-off'" :rules="[rules.required, rules.min]" :type="show1 ? 'text' : 'password'" 
                name="input-10-1" label="Password" hint="At least 8 characters" counter @click:append="show1 = !show1"></v-text-field>
                <v-text-field block v-model="verify" :append-icon="show1 ? 'mdi-eye' : 'mdi-eye-off'" :rules="[rules.required, passwordMatch]" :type="show1 ? 'text' : 'password'" 
                name="input-10-1" label="Confirm Password" counter @click:append="show1 = !show1"></v-text-field>
                <v-btn primary large dark  color="#00528d" class="mt-2" @click="changePwd">Change</v-btn>
                <v-alert class="mt-2" dense :value="alertMsg?true:false" :type="alertType">{{alertMsg}}</v-alert>
              </v-form>
            </v-card-text>
            </v-card>
          </v-card>
          </v-col>
          <v-spacer></v-spacer>
        </v-row>
      </v-container>
        </v-main>
      </v-app>
    </div>
    <script src="https://unpkg.com/axios/dist/axios.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/vue@2.x/dist/vue.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/vuetify@2.x/dist/vuetify.js"></script>
    <script>
      new Vue({
        el: '#app',
        vuetify: new Vuetify(),
  
      computed: {
        passwordMatch() {
          return () => this.password === this.verify || "Password must match";
        }
      },
      methods: {
        changePwd() {
          if (this.$refs.pwdResetForm.validate()) {
            axios.post("${url}", {email: "${email}", code: "${code}", newPassword: this.password})
            .then((response) => {
              if (response.data.success) {
                this.alertType = 'success';
                this.alertMsg = 'Password changed.'
                setTimeout(() => {
                  window.location = '${process.env.CLIENT_DOMAIN}';
                }, 3000);
                
              } else {
                this.alertType = 'error';
                this.alertMsg = response.data.errMsg;
              }
            });
          }
        }
      },
      data(){
        return {
          valid: false,
          password: "",
          verify: "",
          alertMsg: '',
          alertType: 'error',
          show1: false,
          rules: {
            required: value => !!value || "Required.",
            min: v => (v && v.length >= 8) || "Min 8 characters"
          },
          logoDataURI: '${dataURI}'
        }
      }
  
      })
    </script>
  </body>
  </html>
  `
}

export default obj;